import React from 'react';
import {
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Button,
  Box,
  CircularProgress,
  Typography,
} from '@mui/material';
import DownloadIcon from '@mui/icons-material/Download';
import DeleteIcon from '@mui/icons-material/Delete';

interface FileListProps {
  files: Array<{
    name: string;
    url: string;
  }>;
  onDownloadAll: () => void;
  isConverting: boolean;
}

const FileList: React.FC<FileListProps> = ({ files, onDownloadAll, isConverting }) => {
  const handleDownload = (url: string, name: string) => {
    const link = document.createElement('a');
    link.href = url;
    link.download = name;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="h6">Converted Files</Typography>
        <Button
          variant="contained"
          color="primary"
          onClick={onDownloadAll}
          disabled={isConverting}
          startIcon={isConverting ? <CircularProgress size={20} /> : <DownloadIcon />}
        >
          Download All
        </Button>
      </Box>
      <List>
        {files.map((file, index) => (
          <ListItem key={index}>
            <ListItemText
              primary={file.name}
              secondary={isConverting ? 'Converting...' : 'Ready to download'}
            />
            <ListItemSecondaryAction>
              <IconButton
                edge="end"
                aria-label="download"
                onClick={() => handleDownload(file.url, file.name)}
                disabled={isConverting}
              >
                <DownloadIcon />
              </IconButton>
            </ListItemSecondaryAction>
          </ListItem>
        ))}
      </List>
    </Box>
  );
};

export default FileList;
