import React from 'react';
import {
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Typography,
  Box,
} from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';

const faqItems = [
  {
    question: 'What is a HEIC file?',
    answer: 'HEIC (High Efficiency Image Container) is the default image format used by Apple devices. It offers better compression than JPEG while maintaining high quality, but it\'s not universally supported across all platforms and applications.',
  },
  {
    question: 'Why should I convert HEIC to JPEG?',
    answer: 'JPEG is the most widely supported image format across all devices, operating systems, and applications. Converting your HEIC files to JPEG ensures compatibility and makes it easier to share your photos with anyone.',
  },
  {
    question: 'How many files can I convert at once?',
    answer: 'You can convert up to 5 HEIC files at once. This limit helps ensure optimal performance and a smooth user experience.',
  },
  {
    question: 'Is my privacy protected?',
    answer: 'Yes! All conversions happen directly in your browser. Your files never leave your device or get uploaded to any server. We take your privacy seriously.',
  },
  {
    question: 'Is this service really free?',
    answer: 'Yes, our HEIC to JPEG converter is completely free to use. There are no hidden fees or premium features.',
  },
  {
    question: 'Will the quality of my photos be reduced?',
    answer: 'We strive to maintain the original quality of your photos during conversion. While JPEG is a lossy format, we use high-quality settings to minimize any quality loss.',
  },
  {
    question: 'What browsers are supported?',
    answer: 'Our converter works on all modern browsers including Chrome, Firefox, Safari, and Edge. For the best experience, we recommend using the latest version of your preferred browser.',
  },
];

const FAQ: React.FC = () => {
  return (
    <Box sx={{ mt: 6 }}>
      <Typography variant="h5" component="h2" gutterBottom>
        Frequently Asked Questions
      </Typography>
      {faqItems.map((item, index) => (
        <Accordion key={index}>
          <AccordionSummary expandIcon={<ExpandMoreIcon />}>
            <Typography variant="subtitle1">{item.question}</Typography>
          </AccordionSummary>
          <AccordionDetails>
            <Typography color="text.secondary">{item.answer}</Typography>
          </AccordionDetails>
        </Accordion>
      ))}
    </Box>
  );
};

export default FAQ;
