import React, { useState, useCallback } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { Container, Box, Typography, Paper, Alert } from '@mui/material';
import { useDropzone } from 'react-dropzone';
import heic2any from 'heic2any';
import FileList from './components/FileList';
import Header from './components/Header';
import FAQ from './components/FAQ';

const theme = createTheme({
  palette: {
    primary: {
      main: '#2196f3',
    },
    background: {
      default: '#f5f5f5',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
  },
});

interface ConvertedFile {
  name: string;
  url: string;
}

const MAX_FILE_SIZE = 10 * 1024 * 1024; // 10MB in bytes

function App() {
  const [files, setFiles] = useState<ConvertedFile[]>([]);
  const [isConverting, setIsConverting] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const onDrop = useCallback(async (acceptedFiles: File[]) => {
    if (acceptedFiles.length === 0) return;

    setError(null);
    setIsConverting(true);
    const convertedFiles: ConvertedFile[] = [];

    for (const file of acceptedFiles) {
      try {
        if (file.size > MAX_FILE_SIZE) {
          throw new Error(`File ${file.name} is too large. Maximum size is 10MB.`);
        }

        const convertedBlob = await heic2any({
          blob: file,
          toType: 'image/jpeg',
          quality: 0.8,
        }) as Blob;

        const url = URL.createObjectURL(convertedBlob);
        convertedFiles.push({
          name: file.name.replace(/\.heic$/i, '.jpg'),
          url,
        });
      } catch (error) {
        console.error('Error converting file:', error);
        setError(error instanceof Error ? error.message : 'Error converting file');
      }
    }

    setFiles(prevFiles => [...prevFiles, ...convertedFiles]);
    setIsConverting(false);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/heic': ['.heic', '.HEIC'],
    },
    maxFiles: 5,
    maxSize: MAX_FILE_SIZE,
  });

  const handleDownloadAll = () => {
    files.forEach(file => {
      const link = document.createElement('a');
      link.href = file.url;
      link.download = file.name;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    });
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Header />
      <Container maxWidth="md">
        <Box sx={{ my: 4 }}>
          <Typography variant="h4" component="h1" gutterBottom align="center">
            Convert Your iPhone Photos to JPEG - Simple & Free!
          </Typography>
          <Typography variant="subtitle1" align="center" color="text.secondary" paragraph>
            Tired of dealing with .heic files that don't open everywhere? Our free online tool makes it incredibly easy to convert your iPhone photos to the universally compatible .jpeg format. Simply drag and drop your HEIC images, and with a click, download them as JPEGs. No software installation needed, and your privacy is our priority.
          </Typography>

          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}

          <Paper
            {...getRootProps()}
            sx={{
              p: 3,
              mt: 4,
              textAlign: 'center',
              cursor: 'pointer',
              backgroundColor: isDragActive ? 'action.hover' : 'background.paper',
              border: '2px dashed',
              borderColor: isDragActive ? 'primary.main' : 'divider',
            }}
          >
            <input {...getInputProps()} />
            <Typography variant="h6" gutterBottom>
              {isDragActive
                ? 'Drop the files here...'
                : 'Drag & drop up to 5 HEIC files here, or click to select files'}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Maximum 5 files at once, up to 10MB each
            </Typography>
          </Paper>

          {files.length > 0 && (
            <Box sx={{ mt: 4 }}>
              <FileList
                files={files}
                onDownloadAll={handleDownloadAll}
                isConverting={isConverting}
              />
            </Box>
          )}

          <FAQ />
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
